import React, {Component} from "react";
import handler from "uri-js";

class GetAllArticles extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items: []
        };
    }
    componentDidMount(){
        var myHeaders = new Headers();
        myHeaders.append('Accept', '*/*');
        myHeaders.append('Authorization', 'Basic cHJlc3RhdGFpcmVAYm5ic2l0dGVyLmNvbToxMjM0NTY=');

        fetch("http://localhost:8080/v1/api/cvindexer/allCV", {
            mode: 'no-cors',
            method:'GET',
            headers: myHeaders
        }
        ).then(res => console.log(res.json()))
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        items: result.items
                    });
                },
                // Remarque : il est important de traiter les erreurs ici
                // au lieu d'utiliser un bloc catch(), pour ne pas passer à la trappe
                // des exceptions provenant de réels bugs du composant.
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }
    render() {
        const { error, isLoaded, items } = this.state;
        return (
            <div className="articles-list">
                <h1>Bienvenue sur la CVthèque</h1>
                <ul>
                    <li>Instagram</li>
                    <li>WhatsApp</li>
                    <li>Oculus</li>
                </ul>

                <ul>
                    {items.map(item => (
                        <li >
                            {item.toString()}
                        </li>
                    ))}
                </ul>
            </div>
        );
    }
}

export default GetAllArticles;
