import React, {Component} from "react";
import Cv from './Cv';
import axios from "axios";

class Research extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: "",
            cvs :[]
        };
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    async handleSubmit(event) {
        event.preventDefault();
        alert('Le nom a été soumis : ' + this.state.value);

        const url = '/v1/api/cvindexer/research';

        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }

        const response = await axios.get(url, { params: { research: this.state.value } });
        const body = await response.json();
        console.log(body);
        this.setState({cvs : body});
    }

    render() {
        const { cvs } = this.state;
        const nouveauCvs = [];
        for(var i = 0; i < cvs.length; i++){
            nouveauCvs.push(<Cv key={i} id={i} fileName={cvs[i].filename} content={cvs[i].content} />);
        }
        return (
            <div>
                <div className="cvs-list">
                    <h2>Rechercher</h2>
                    <div>
                        <form onSubmit={this.handleSubmit}>
                            <label>
                                Rechercher (vous pouvez rechercher plusieurs termes en faisant ainsi terme1;terme2):
                                <input type="text" value={this.state.value} onChange={this.handleChange}  />
                            </label>
                            <br />
                            <button type="submit">Envoyer</button>
                        </form>
                    </div>
                </div>
                <div className="cvs-list">
                    <h2>CV Listes</h2>
                    {nouveauCvs}
                </div>
            </div>
        );
    }
}

export default Research;
