import React, {Component} from "react";
import axios from 'axios';
import Alert from "react-bootstrap/Alert";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/cjs/Button";

class InsertFile extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            message : null,
        };
        this.handleSubmit = this.handleSubmit.bind(this);
        this.fileInput = React.createRef();
    }

    async handleSubmit(event) {
        event.preventDefault();
        alert(
            `Fichier sélectionné - ${        this.fileInput.current.files[0].name
            }`

        );

        const url = '/v1/api/cvindexer/upload';
        const formData = new FormData();
        formData.append('file',this.fileInput.current.files[0])
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }
        const response = await axios.post(url, formData,config);
        let message = "";
        if(response.status == 201) {
           message = <Alert variant="success">
                    <Alert.Heading>{response.statusText}</Alert.Heading>
                    <p>
                        Le cv a bien été ajouté.
                    </p>
                </Alert>
            ;
        }else {
            message = <Alert variant="danger">
                <Alert.Heading>{response.statusText}</Alert.Heading>
                <p>
                    Erreur lors de l'insertion
                </p>
            </Alert>
            ;
        }
        this.setState({message : message});
    }

    render() {
        const {message} = this.state;
        return (
            <div className="research-cv">
                <h2>Enregistrer un cv</h2>
                <div style={{color : "red"}}>{message}</div>
                <Form onSubmit={this.handleSubmit}>
                    <Form.Group>
                        <Form.Label>Envoyer un fichier :</Form.Label>
                        <input accept="application/pdf,application/vnd.openxmlformats-officedocument.wordprocessingml.document" type="file" ref={this.fileInput} />
                    </Form.Group>
                    <br />
                    <Button type="submit">Envoyer</Button>
                </Form>
            </div>
        );
    }
}

export default InsertFile;
