import React, {Component} from "react";
import Cv from './Cv';
class GetAllCvs extends Component {
    constructor(props) {
        super(props);
        this.state = {
            cvs: []
        };
    }
    async componentDidMount() {
        const response = await fetch('/v1/api/cvindexer/allCV');
        const body = await response.json();
        this.setState({cvs : body});

    }

    render() {
        const { cvs } = this.state;
        const nouveauCvs = [];
        for(var i = 0; i < cvs.length; i++){
            nouveauCvs.push(<Cv key={i} id={i} fileName={cvs[i].filename} content={cvs[i].content} />);
        }

        return (
            <div className="cvs-list">
                <h2>CV Listes</h2>
                {nouveauCvs}
            </div>
        );
    }
}

export default GetAllCvs;
