import React, {Component} from "react";
import axios from 'axios';

class InsertFile extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            message : null,
        };
        this.handleSubmit = this.handleSubmit.bind(this);
        this.fileInput = React.createRef();
    }

    async handleSubmit(event) {
        event.preventDefault();
        alert(
            `Fichier sélectionné - ${        this.fileInput.current.files[0].name
            }`

        );

        const url = '/v1/api/cvindexer/upload';
        const formData = new FormData();
        formData.append('file',this.fileInput.current.files[0])
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }
        const response = await axios.post(url, formData,config);
        this.state({message : response.statusText});
    }

    render() {
        const {message} = this.state;
        return (
            <div className="research-cv">
                <h2>Recherche</h2>
                <div style={{color : "red"}}>{message}</div>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        Envoyer un fichier :
                        <input type="file" ref={this.fileInput} />
                    </label>
                    <br />
                    <button type="submit">Envoyer</button>
                </form>
            </div>
        );
    }
}

export default InsertFile;
