import React, {Component} from "react";

class InsertFile extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            cvs: []
        };
        this.handleSubmit = this.handleSubmit.bind(this);
        this.fileInput = React.createRef();
    }

    async handleSubmit(event) {
        event.preventDefault();
        alert(
            `Fichier sélectionné - ${        this.fileInput.current.files[0].name
            }`

        );
        // Create an object of formData
        const formData = new FormData();

        // Update the formData object
        formData.append(
            "file",
            this.fileInput.current[0],
            this.fileInput.current[0].name
        );


        // Details of the uploaded file
        console.log(this.fileInput.current[0]);
        const response = await fetch('/v1/api/cvindexer/upload');
        const body = await response.json();
        console.log(body);
    }

    render() {
        return (
            <div className="research-cv">
                <h2>Recherche</h2>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        Envoyer un fichier :
                        <input type="file" ref={this.fileInput} />
                    </label>
                    <br />
                    <button type="submit">Envoyer</button>
                </form>
            </div>
        );
    }
}

export default InsertFile;
